    <x-auth-header :title="__('Fordele der virker â€“ hver dag')"/>

    <!-- Session Status -->
    <x-auth-session-status class="text-center" :status="session('status')" />

    <form wire:submit="login" class="flex flex-col gap-6 mt-6">
        <!-- Email Address -->
        <flux:input
            wire:model="email"
            :label="__('Email')"
            type="email"
            required
            autofocus
            autocomplete="email"
        />

        <!-- Password -->
        <div class="relative">
            <flux:input
                wire:model="password"
                :label="__('Adgangskode')"
                type="password"
                required
                autocomplete="current-password"
            />

            @if (Route::has('password.request'))
                <flux:link class="absolute right-0 top-0 !text-xs !no-underline" :href="route('password.request')" wire:navigate>
                    {{ __('Glemt adgangskode') }}
                </flux:link>
            @endif
        </div>

        <!-- Remember Me -->
        {{-- <flux:checkbox wire:model="remember" :label="__('Remember me')" /> --}}

        <div class="flex items-center justify-end mt-6">
            <button type="submit" class="btn btn--primary w-full">
                {{ __('Log in') }}
            </button>
        </div>
    </form>

    {{-- @if (Route::has('register'))
        <div class="space-x-1 text-center text-sm text-zinc-600">
            {{ __('Don\'t have an account?') }}
            <flux:link class="text-primary" :href="route('register')" wire:navigate>{{ __('Sign up') }}</flux:link>
        </div>
    @endif --}}
